// DOM elements
const monasteryList = document.getElementById("monasteryList");
const audioPlayer = document.getElementById("audioPlayer");
const audioSource = document.getElementById("audioSource");
const caption = document.getElementById("caption");
const languageSelector = document.getElementById("languageSelector");

// Default language
let currentLanguage = "english";

// Change language on dropdown select
languageSelector.addEventListener("change", (e) => {
  currentLanguage = e.target.value;
});

// Create buttons dynamically
monasteries.forEach(monastery => {
  const btn = document.createElement("button");
  btn.innerText = monastery.name;
  btn.onclick = () => {
    caption.innerText = ""; // reset caption
    audioSource.src = monastery.files[currentLanguage];
    audioPlayer.load();
    audioPlayer.play();

    // Show caption once audio starts
    audioPlayer.onplay = () => {
      caption.innerText = monastery.captions[currentLanguage];
    };
  };
  monasteryList.appendChild(btn);
});